import React, { useEffect } from 'react'
import logo from './logo.svg'
import './app.css'
import ReactDOM from 'react-dom'
import Content from './content'
import { Provider } from 'react-redux'
import globalState from './redux/state'
import { html } from 'htm/react'


function globalHoverSelectHandler(e: any) {
    const isDropdownButton = e.target.matches("[data-hoverSelect-button]")
    if (!isDropdownButton && e.target.closest("[data-hoverSelect]") != null) return

    let currentDropdown: any = null
    if (isDropdownButton) {
        currentDropdown = e.target.closest("[data-hoverSelect]")
        currentDropdown.classList.toggle("active")
    }

    document.querySelectorAll("[data-hoverSelect].active").forEach(dropdown => {
    if (dropdown === currentDropdown) return
        dropdown.classList.remove("active")
    })
}


const App: React.FunctionComponent = () => {
    useEffect(() => {
        document.addEventListener("click", globalHoverSelectHandler)
    }, [])
    return html`
        <${Provider} store=${globalState}>
            <${Content} />
        <//>
    `
}


ReactDOM.render(
    html`<${React.StrictMode}>
      <${App} />
    <//>`,    
    document.getElementById("snippetRoot")
)
